import React, { Component } from "react";
import Hero from "../components/Hero/heroComponent";
import Banner from "../components/banner/banner";
import { Link } from "react-router-dom";
import { RoomContext } from "../context/context";
import StyledeHero from "../components/StyledHero/styledHero";
import Image from "../components/ImageComponent/Image";
import {addDays, format} from 'date-fns';

class SingleRoom extends Component {
  static contextType = RoomContext;
  constructor(props) {
    super(props);
    this.state = {
      slug: this.props.match.params.slug,
      defaultBcg: "",
      showTable: false,
      selectedPlaces: new Set(),
      unavailablePlaces: new Set(),
    };
  }

  toggle = (src) => {
    this.setState({ defaultBcg: src });
  };

  handleReserveClick = () => {
    this.setState({ showTable: true });
  };

  handlePlaceClick = (place) => {
    const { selectedPlaces, unavailablePlaces } = this.state;
    const updatedSelectedPlaces = new Set(selectedPlaces);

    if (unavailablePlaces.has(place)) {
      return;
    }

    if (updatedSelectedPlaces.has(place)) {
      updatedSelectedPlaces.delete(place);
    } 
    else {
      updatedSelectedPlaces.add(place);
    }

    this.setState({ selectedPlaces: updatedSelectedPlaces });
  };

  handleBuyClick = () => {
    // Convert the set of selected places to the next 30 days from now
    const startDate = new Date();
    const selectedDates = Array.from(this.state.selectedPlaces).map((place) =>
      format(addDays(startDate, place), 'MMMM d, yyyy')
    );

    console.log('Selected Dates:', selectedDates);
    let newUnavailablePlaces = new Set(this.state.unavailablePlaces);
    newUnavailablePlaces.add(this.state.selected);
    this.setState({unavailablePlaces: newUnavailablePlaces});

    this.setState({showTable: false});
    this.setState({selectedPlaces: new Set()});
  };

  render() {
    console.log("slug", this.state.slug);
    const room = this.context.getRoom(this.state.slug);
    const { showTable, selectedPlaces, unavailablePlaces } = this.state;

    if (!room) {
      return (
        <div className="error">
          <h3>no such room could be found....</h3>
          <Link to="/rooms" className="btn-primary">
            Rooms Page
          </Link>
        </div>
      );
    }
    const {
      name,
      description,
      capacity,
      size,
      price,
      extras,
      breakfast,
      pets,
      images,

    } = room;
    const [mainImg, ...defaultImg] = images;
    return (
      <>
        <StyledeHero img={this.state.defaultBcg} hero="roomsHero">
          <Banner title={`${name} room`}>
            <Link to="/rooms" className="btn-primary">
              Rooms Page
            </Link>
          </Banner>
        </StyledeHero>

        <section className="single-room">
          <div className="single-room-images">
            {defaultImg.map((item, index) => (
              <Image toggle={this.toggle} item={item} key={index} name={name} />
            ))}
          </div>
          <div className="single-room-info">
            <article className="desc">
              <h3>detais</h3>
              <p>{description}</p>
            </article>
            <article className="info">
              <h3> info</h3>
              <h6> price : ${price}</h6>
              <h6> size : {size}</h6>
              <h6> max capacity :{capacity} person</h6>
              <h6> {pets ? "pets allowed" : "no pets allowed"}</h6>
              <h6> {breakfast && "free breackfast"}</h6>
            </article>
          </div>
        </section>
        <section className="room-extras">
          <h6> extras</h6>
          <ul className="extras">
            {extras.map((item, index) => {
              return <li key={index}>-{item}</li>;
            })}
          </ul>
        </section>
        <div className="flex items-center justify-center h-screen">
        {!showTable ? (
          <button
            onClick={this.handleReserveClick}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 focus:outline-none focus:ring"
          >
            Reserve
          </button>
        ) : (
          <div>
            <table className="border-collapse border border-gray-500 w-full mt-4">
              <tbody>
                {[...Array(30)].map((_, index) => (
                  <tr
                    key={index}
                    className="border border-gray-500"
                    onClick={() => this.handlePlaceClick(index + 1)}
                    style={{
                      backgroundColor: selectedPlaces.has(index + 1) ? '#48BB78' : unavailablePlaces.has(index + 1) ? 'red' : 'transparent',
                      cursor: unavailablePlaces.has(index + 1) ? 'not-allowed' : 'pointer',
                    }}
                  > 
                    <td className="p-2">{format(addDays(new Date(), index + 1), 'MMM d')}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <button
              onClick={this.handleBuyClick}
              className="bg-green-500 text-white px-4 py-2 rounded mt-4 hover:bg-green-600 focus:outline-none focus:ring"
              disabled={selectedPlaces.size === 0}
            >
              Buy
            </button>
          </div>
        )}
        </div>
      </>
    );
  }
}

export default SingleRoom;
