import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import {useParams} from 'react-router-dom';

function MainPage() {

    constusername, loggedIn = useParams();
    const navigate = useNavigate();

    const handleLoginClick = () => {
        navigate('/login');
    };

    return (
        <div>
        <header style={headerStyle}>
            <nav style={menuStyle}>
            <ul style={menuListStyle}>
                <li style={menuItemStyle}>First</li>
                <li style={menuItemStyle}>Second</li>
                <li style={menuItemStyle}>Third</li>
                <li style={menuItemStyle}>Fourth</li>
            </ul>
            </nav>
            {!loggedIn &&
            <div style={buttonStyle}>
                <button onClick={handleLoginClick}>Login</button>
                <button >Sign Up</button>
            </div>
            }
            {loggedIn &&
            <div>{username}</div>
            }
        </header>
        <section style={sectionStyle}>
            <h1>Welcome to My Hotel</h1>
            <p>Explanations about your hotel go here.</p>
        </section>
        </div>
    );
}

// CSS styles
const headerStyle = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  padding: '20px',
  backgroundColor: '#f2f2f2',
};

const menuStyle = {
  display: 'flex',
};

const menuListStyle = {
  listStyle: 'none',
  display: 'flex',
};

const menuItemStyle = {
  marginRight: '10px',
};

const buttonStyle = {
  marginLeft: '10px',
};

const sectionStyle = {
  padding: '20px',
};

export default MainPage;
