import React, { useState } from 'react';
//import {useNavigate} from 'react-router-dom';
import { useHistory } from 'react-router-dom';

const URL = "http://localhost:8000/"

async function sendRequestToServer(data, header) {
    try {
        const response = await fetch(URL + header, {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json',
            },
            body: JSON.stringify(data),
        });

        if (!response.ok) {
            // Handle server errors
            throw new Error(`Request failed with status ${response.status}`);
        }

        const responseData = await response.json();

        // Handle the response data from the server
        console.log(responseData);
        // Perform further operations with the response data
        return responseData;
    } 
    catch (error) {
    // Handle any errors during the request
    console.error('Error:', error);
    }
    return null;
}

function setUserInCookie(user) {
    const expirationDate = new Date();
    // expire after 7 days
    expirationDate.setDate(expirationDate.getDate() + 7);
    const cookieValue = encodeURIComponent(user);
    const cookieName = "user";
    const cookieOptions = {
        expires: expirationDate.toUTCString(),
        path: "/"
    };
    document.cookie = `${cookieName}=${cookieValue}; ${Object.entries(cookieOptions)
        .map(([key, value]) => `${key}=${value}`)
        .join("; ")}`;
}

function SignUpPage() {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [repeatePassword, setRepeatPassword] = useState('');
    let history = useHistory();
    //const navigate = useNavigate();

    const handleBackClick = () => {
        //navigate('/');
        history.push('/');
    };

    const handleUsernameChange = (event) => {
        setUsername(event.target.value);
    };

    const handlePasswordChange = (event) => {
        setPassword(event.target.value);
    };

    const handleRepeatPasswordChange = (event) => {
        setRepeatPassword(event.target.value);
    }

    const handleSignUp = (event) => {
        event.preventDefault();
        // todo: check if username already exists in database
        // todo: save user's information in database
        // Perform authentication logic here
        console.log('Username:', username);

        console.log('Password:', password);

        console.log('Repeated Password:', repeatePassword);

        let data = {
            username: username,
            password: password,
        };

        let response = sendRequestToServer(data, 'sign-up/');
        if (response.success) {
            // profile successfully created.
            // save it in cookie
            let user = {
                username: username,
                password: password,
                credit: 0,
            }
            setUserInCookie(user);
        }
        else {
            alert(response.message);
        }
        // Redirect to authenticated page or perform further actions
        history.push('/');
    };

    return (
        <div style={containerStyle}>
        <h2>SignUp</h2>
        <form onSubmit={handleSignUp}>
            <label>
            Username:
            <input
                type="text"
                value={username}
                onChange={handleUsernameChange}
                style={inputStyle}
            />
            </label>
            <br />
            <label>
            Password:
            <input
                type="password"
                value={password}
                onChange={handlePasswordChange}
                style={inputStyle}
            />
            </label>
            <br />
            <label>
                Repeat Password:
                <input
                    type="password"
                    value={repeatePassword}
                    onChange={handleRepeatPasswordChange}
                    style={inputStyle}
                />
            </label>
            <br />
            <button type="submit" style={buttonStyle}>Login</button>
        </form>
        <br />
        <button onClick={handleBackClick} type="submit" style={buttonStyle}>Back</button>
        </div>
    );
}

// CSS styles
const containerStyle = {
  width: '300px',
  margin: 'auto',
  marginTop: '100px',
  padding: '20px',
  border: '1px solid #ccc',
  borderRadius: '5px',
  backgroundColor: '#f2f2f2',
};

const inputStyle = {
  display: 'block',
  width: '100%',
  padding: '8px',
  marginBottom: '10px',
};

const buttonStyle = {
  display: 'block',
  width: '100%',
  padding: '10px',
  backgroundColor: '#4caf50',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
};

export default SignUpPage;
